00052 PRISM_Triangle AbstractObject3D::RotateTriangle(struct PRISM_Triangle Triangle_) {
00053     struct PRISM_Triangle TriangleRotatedZ, TriangleRotatedZX, TriangleRotatedZXY;
00054     // Rotate in Z-Axis
00055     Math::MultiplyMatrixVector(Triangle_.a, TriangleRotatedZ.a, matrixRotZ);
00056     Math::MultiplyMatrixVector(Triangle_.b, TriangleRotatedZ.b, matrixRotZ);
00057     Math::MultiplyMatrixVector(Triangle_.c, TriangleRotatedZ.c, matrixRotZ);
00058     
00059     // Rotate in X-Axis
00060     Math::MultiplyMatrixVector(TriangleRotatedZ.a, TriangleRotatedZX.a, matrixRotX);
00061     Math::MultiplyMatrixVector(TriangleRotatedZ.b, TriangleRotatedZX.b, matrixRotX);
00062     Math::MultiplyMatrixVector(TriangleRotatedZ.c, TriangleRotatedZX.c, matrixRotX);
00063     
00064     // Rotate in Y-Axis
00065     Math::MultiplyMatrixVector(TriangleRotatedZX.a, TriangleRotatedZXY.a, matrixRotY);
00066     Math::MultiplyMatrixVector(TriangleRotatedZX.b, TriangleRotatedZXY.b, matrixRotY);
00067     Math::MultiplyMatrixVector(TriangleRotatedZX.c, TriangleRotatedZXY.c, matrixRotY);
00068     return TriangleRotatedZXY;
