00100 PRISM_Color AbstractObject3D::CalculatePhongShadingColor(const PRISM_Vector3d& normal, const PRISM_Vector3d& viewDir,
00101                                                          const PRISM_Light& light, const PRISM_Vector3d& fragPos) {
00102     // Ambient component
00103     float ambientStrength = 0.1f;
00104     PRISM_Color ambient = { static_cast<Uint8>(light.color.r * ambientStrength),
00105                       static_cast<Uint8>(light.color.g * ambientStrength),
00106                       static_cast<Uint8>(light.color.b * ambientStrength),
00107                       light.color.a };
00108     
00109     // Light direction (from fragment position to light position)
00110     PRISM_Vector3d lightDir = { light.position.x - fragPos.x, light.position.y - fragPos.y, light.position.z - fragPos.z };
00111     float lightLen = sqrt(lightDir.x * lightDir.x + lightDir.y * lightDir.y + lightDir.z * lightDir.z);
00112     lightDir = { lightDir.x / lightLen, lightDir.y / lightLen, lightDir.z / lightLen };
00113     
00114     // Diffuse component
00115     float diff = std::max(0.0f, lightDir.x * normal.x + lightDir.y * normal.y + lightDir.z * normal.z);
00116     PRISM_Color diffuse = { static_cast<Uint8>(light.color.r * diff),
00117                       static_cast<Uint8>(light.color.g * diff),
00118                       static_cast<Uint8>(light.color.b * diff),
00119                       light.color.a };
00120     
00121     // Reflect direction
00122     PRISM_Vector3d reflectDir = { lightDir.x - 2 * normal.x * diff,
00123                             lightDir.y - 2 * normal.y * diff,
00124                             lightDir.z - 2 * normal.z * diff };
00125     float spec = std::pow(std::max(0.0f, reflectDir.x * viewDir.x + reflectDir.y * viewDir.y + reflectDir.z * viewDir.z), 32);
00126     PRISM_Color specular = { static_cast<Uint8>(light.color.r * spec),
00127                        static_cast<Uint8>(light.color.g * spec),
00128                        static_cast<Uint8>(light.color.b * spec),
00129                        light.color.a };
00130     
00131     // Final color
00132     PRISM_Color result = { static_cast<Uint8>(std::min(255, ambient.r + diffuse.r + specular.r)),
00133                      static_cast<Uint8>(std::min(255, ambient.g + diffuse.g + specular.g)),
00134                      static_cast<Uint8>(std::min(255, ambient.b + diffuse.b + specular.b)),
00135                      light.color.a };
00136     return result;
00137 }
