00290 // Функция для вычисления барицентрических координат
00291 bool AbstractObject3D::ComputeBarycentricCoords(float x, float y, const PRISM_Vector3d& v0, const PRISM_Vector3d& v1, const PRISM_Vector3d& v2, float& u, float& v, float& w) {
00292     float denom = (v1.y - v2.y)*(v0.x - v2.x) + (v2.x - v1.x)*(v0.y - v2.y);
00293     u = ((v1.y - v2.y)*(x - v2.x) + (v2.x - v1.x)*(y - v2.y)) / denom;
00294     v = ((v2.y - v0.y)*(x - v2.x) + (v0.x - v2.x)*(y - v2.y)) / denom;
00295     w = 1.0f - u - v;
00296     return u >= 0 && v >= 0 && w >= 0;
00297 }
