00300 void AbstractObject3D::OldRasterisation(int x1, int y1, int x2, int y2, int x3, int y3, float z1, float z2, float z3,
00301                    SDL_Renderer* SDL_renderer, PRISM_Light light, short rgba[4], PRISM_RenderMode RendMode) {
00302     PRISM_Vector3d v0 = {static_cast<float>(x1), static_cast<float>(y1), static_cast<float>(z1)};
00303     PRISM_Vector3d v1 = {static_cast<float>(x2), static_cast<float>(y2), static_cast<float>(z2)};
00304     PRISM_Vector3d v2 = {static_cast<float>(x3), static_cast<float>(y3), static_cast<float>(z3)};
00305     
00306     PRISM_Vector3d normal = Math::CalculateNormals({v0, v1, v2});
00307     PRISM_Vector3d viewDir = {0, 0, 1};  // Assuming the camera looks along the negative Z axis
00308     
00309     PRISM_Color color0, color1, color2;
00310     if(RendMode.DisplayDimming2) {
00311         ;
00312     }
00313     // Calculate color at each vertex
00314     
00315     // Замер времени
00316     auto start = std::chrono::high_resolution_clock::now();
00317     
00318     // Compute bounding box of the triangle
00319     float minX = std::min({v0.x, v1.x, v2.x});
00320     float maxX = std::max({v0.x, v1.x, v2.x});
00321     float minY = std::min({v0.y, v1.y, v2.y});
00322     float maxY = std::max({v0.y, v1.y, v2.y});
00323 
00324     
00325     for (int y = static_cast<int>(std::ceil(minY));
00326          y <= static_cast<int>(std::floor(maxY)); ++y) {
00327         for (int x = static_cast<int>(std::ceil(minX));
00328              x <= static_cast<int>(std::floor(maxX)); ++x) {
00329             float u, v, w;
00330             if (ComputeBarycentricCoords(static_cast<float>(x), static_cast<float>(y), v0,
00331                                          v1, v2, u, v, w)) {
00332                 float z = u * v0.z + v * v1.z + w * v2.z;
00333                 if (z < Camera.z_buffer.get(x, y) | !RendMode.Z_Buffer) {
00334                     Uint8 r = rgba[0];
00335                     Uint8 g = rgba[1];
00336                     Uint8 b = rgba[2];
00337                     Uint8 a = rgba[3];
00338                     if(RendMode.DisplayDimming2) {
00339                         ;
00340                     }
00341 
00342                     SDL_SetRenderDrawColor(SDL_renderer, r, g, b, a);
00343                     SDL_RenderDrawPoint(SDL_renderer, x, y);
00344                     Camera.z_buffer.update(x, y, z);
00345                 }
00346             }
00347         }
00348     }
00349     auto end = std::chrono::high_resolution_clock::now();
00350     std::chrono::duration<double, std::milli> duration = end - start;
00351     
00352     // Вывод времени через SDL_Log
00353     SDL_Log("Время выполнения: %.3f мс", duration.count());
00354 }
