00356 void AbstractObject3D::OptimizedEdgeRasterization(int x1, int y1, int x2, int y2, int x3, int y3, float z1, float z2, float z3,
00357                                                   SDL_Renderer* SDL_renderer, PRISM_Light light, short rgba[4], PRISM_RenderMode RendMode) {
00358     PRISM_Vector3d v0 = {static_cast<float>(x1), static_cast<float>(y1), static_cast<float>(z1)};
00359     PRISM_Vector3d v1 = {static_cast<float>(x2), static_cast<float>(y2), static_cast<float>(z2)};
00360     PRISM_Vector3d v2 = {static_cast<float>(x3), static_cast<float>(y3), static_cast<float>(z3)};
00361     // Сортировка вершин по y (v0.y <= v1.y <= v2.y)
00362     if (v1.y < v0.y) std::swap(v0, v1);
00363     if (v2.y < v0.y) std::swap(v0, v2);
00364     if (v2.y < v1.y) std::swap(v1, v2);
00365     // Функция для интерполяции X-координаты по рёбрам треугольника
00366     auto interpolateX = [](float x0, float y0, float x1, float y1, float y) -> float {
00367     if (y0 == y1) return x0; // Избегаем деления на ноль
00368         return x0 + (x1 - x0) * (y - y0) / (y1 - y0);
00369     };
00370     
00371     // Инкрементальная растеризация: делим треугольник на два части (верхняя и нижняя)
00372     auto rasterize_half = [&](PRISM_Vector3d v0, PRISM_Vector3d v1, PRISM_Vector3d v2) {
00373         for (int y = static_cast<int>(std::ceil(v0.y)); y <= static_cast<int>(std::floor(v2.y)); ++y) {
00374             float xStart = interpolateX(v0.x, v0.y, v2.x, v2.y, y);
00375             float xEnd = interpolateX(v1.x, v1.y, v2.x, v2.y, y);
00376             if (xStart > xEnd) std::swap(xStart, xEnd);
00377             for (int x = static_cast<int>(std::ceil(xStart)); x <= static_cast<int>(std::floor(xEnd)); ++x) {
00378                 float u, v, w;
00379                 if (ComputeBarycentricCoords(static_cast<float>(x), static_cast<float>(y), v0, v1, v2, u, v, w)) {
00380                     float z = u * v0.z + v * v1.z + w * v2.z;
00381                     if (z < Camera.z_buffer.get(x, y) || !RendMode.Z_Buffer) {
00382                         Uint8 r = rgba[0];
00383                         Uint8 g = rgba[1];
00384                         Uint8 b = rgba[2];
00385                         Uint8 a = rgba[3];
00386                         
00387                         SDL_SetRenderDrawColor(SDL_renderer, r, g, b, a);
00388                         SDL_RenderDrawPoint(SDL_renderer, x, y);
00389                         Camera.z_buffer.update(x, y, z);  // Обновляем Z-буфер
00390                     }
00391                 }
00392             }
00393         }
00394     };
00395     // Растеризуем верхний и нижний треугольники
00396     rasterize_half(v0, v1, v2);  // Нижняя часть треугольника
00397     rasterize_half(v1, v1, v2);  // Верхняя часть
00398 }
