00401 // Обертка для вызова растеризации в отдельном потоке
00402 void AbstractObject3D::RasterizeTile(int minX, int maxX, int minY, int maxY,
00403                    const PRISM_Vector3d& v0, const PRISM_Vector3d& v1, const PRISM_Vector3d& v2,
00404                    SDL_Renderer* SDL_renderer, PRISM_Light light, short rgba[4],
00405                    PRISM_RenderMode RendMode, std::vector<SDL_Surface*>& surfaces) {
00406     
00407     std::mutex render_mutex;
00408     // Создание поверхности для текущего тайла
00409     SDL_Surface* RasterSurface = SDL_CreateRGBSurface(0, maxX - minX + 1, maxY - minY + 1, 32, 0x00FF0000, 0x0000FF00, 0x000000FF, 0xFF000000);
00410     if (SDL_LockSurface(RasterSurface) != 0) {
00411         SDL_Log("Unable to lock surface: %s", SDL_GetError());
00412         SDL_FreeSurface(RasterSurface);
00413         return ;
00414     }
00415     Uint32* pixels = (Uint32*)RasterSurface->pixels;
00416     for (int y = minY; y <= maxY; ++y) {
00417         for (int x = minX; x <= maxX; ++x) {
00418             float u, v, w;
00419             if (ComputeBarycentricCoords(static_cast<float>(x), static_cast<float>(y), v0, v1, v2, u, v, w)) {
00420                 float z = u * v0.z + v * v1.z + w * v2.z;
00421                 
00422                 render_mutex.lock();
00423                 int z_buff_point = Camera.z_buffer.get(x, y);
00424                 if (z < z_buff_point | !RendMode.Z_Buffer) {
00425                     Uint8 r = rgba[0];
00426                     Uint8 g = rgba[1];
00427                     Uint8 b = rgba[2];
00428                     Uint8 a = rgba[3];
00429                     
00430                     if (RendMode.DisplayDimming2) {
00431                         // Применение затемнения, если это необходимо
00432                     }
00433                     
00434                     Uint32 color = SDL_MapRGB(RasterSurface->format, r, g, b);
00435                     pixels[(y - minY) * RasterSurface->w + (x - minX)] = color;
00436                     
00437                     Camera.z_buffer.update(x, y, z);
00438                 }
00439                 render_mutex.unlock();
00440             }
00441         }
00442     }
00443     
00444     SDL_UnlockSurface(RasterSurface);
00445     
00446     render_mutex.lock();
00447     surfaces.push_back(RasterSurface);
00448     render_mutex.unlock();
00449 }
