00452 void AbstractObject3D::Rasterisation(int x1, int y1, int x2, int y2, int x3, int y3, float z1, float z2, float z3,
00453                    SDL_Renderer* SDL_renderer, PRISM_Light light, short rgba[4], PRISM_RenderMode RendMode) {
00454     PRISM_Vector3d v0 = {static_cast<float>(x1), static_cast<float>(y1), static_cast<float>(z1)};
00455     PRISM_Vector3d v1 = {static_cast<float>(x2), static_cast<float>(y2), static_cast<float>(z2)};
00456     PRISM_Vector3d v2 = {static_cast<float>(x3), static_cast<float>(y3), static_cast<float>(z3)};
00457     PRISM_Vector3d normal = Math::CalculateNormals({v0, v1, v2});
00458     PRISM_Vector3d viewDir = {0, 0, 1};  // Предполагается, что камера смотрит вдоль отрицательной оси Z
00459     
00460     // Вычисление ограничивающего прямоугольника треугольника
00461     float minX = std::min({v0.x, v1.x, v2.x});
00462     float maxX = std::max({v0.x, v1.x, v2.x});
00463     float minY = std::min({v0.y, v1.y, v2.y});
00464     float maxY = std::max({v0.y, v1.y, v2.y});
00465     
00466     // Делим область на 4 части
00467     int midX = static_cast<int>((minX + maxX) / 2);
00468     int midY = static_cast<int>((minY + maxY) / 2);
00469     
00470 //          // Векторы потоков
00471 //          std::vector<std::thread> threads;
00472     std::vector<SDL_Surface*> surfaces;
00473 
00474     // Запускаем 4 потока для обработки четырех квадрантов
00475     std::thread ta(&AbstractObject3D::RasterizeTile, this, minX, midX, minY, midY, v0, v1, v2, SDL_renderer, light, rgba, RendMode, std::ref(surfaces));
00476     std::thread tb(&AbstractObject3D::RasterizeTile, this, midX + 1, maxX, minY, midY, v0, v1, v2, SDL_renderer, light, rgba, RendMode, std::ref(surfaces));
00477     std::thread tc(&AbstractObject3D::RasterizeTile, this, minX, midX, midY + 1, maxY, v0, v1, v2, SDL_renderer, light, rgba, RendMode, std::ref(surfaces));
00478     std::thread td(&AbstractObject3D::RasterizeTile, this, midX + 1, maxX, midY + 1, maxY, v0, v1, v2, SDL_renderer, light, rgba, RendMode, std::ref(surfaces));
00479     
00480     ta.join();
00481     tb.join();
00482     tc.join();
00483     td.join();
00484     
00485     // Ждем завершения всех потоков
00486 //          for (auto& t : threads) {
00487 //              t.join();
00488 //          }
00489     
00490     int surfIndex = 0;
00491     // Теперь surfaces содержит surface, созданные в потоках
00492     for (SDL_Surface* surface : surfaces) {
00493         // Создание текстуры из поверхности
00494         SDL_Texture* texture = SDL_CreateTextureFromSurface(SDL_renderer, surface);
00495         
00496         // Освобождение поверхности
00497         SDL_FreeSurface(surface);
00498         SDL_Rect dstrect;
00499         switch (surfIndex) {
00500             case 0:
00501                 dstrect = {(int)minX, (int)minY, (int)midX - (int)minX, (int)midY - (int)minY};
00502                 break;
00503             case 1:
00504                 dstrect = {(int)midX + 1, (int)minY, (int)maxX - (int)midX + 1, (int)midY - (int)minY};
00505                 break;
00506             case 2:
00507                 dstrect = {(int)minX, (int)midY + 1, (int)midX - (int)minX, (int)maxY - (int)midY + 1};
00508                 break;
00509             case 3:
00510                 dstrect = {(int)midX + 1, (int)midY + 1, (int)maxX - (int)midX + 1, (int)maxY - (int)midY + 1};
00511                 break;
00512         }
00513      
00514         SDL_RenderCopy(SDL_renderer, texture, nullptr, &dstrect);
00515         SDL_DestroyTexture(texture);
00516         surfIndex++;
00517     }
00518 }
