00520 void AbstractObject3D::TileRasterisation(int x1, int y1, int x2, int y2, int x3, int y3,
00521                                         float z1, float z2, float z3, SDL_Renderer* SDL_renderer,
00522                                         PRISM_Light light, short rgba[4], PRISM_RenderMode RendMode) {
00523     PRISM_Vector3d v0 = {static_cast<float>(x1), static_cast<float>(y1), static_cast<float>(z1)};
00524     PRISM_Vector3d v1 = {static_cast<float>(x2), static_cast<float>(y2), static_cast<float>(z2)};
00525     PRISM_Vector3d v2 = {static_cast<float>(x3), static_cast<float>(y3), static_cast<float>(z3)};
00526     
00527     // Calculate the normal and other necessary values
00528     PRISM_Vector3d normal = Math::CalculateNormals({v0, v1, v2});
00529     PRISM_Vector3d viewDir = {0, 0, 1};  // Assuming the camera looks along the negative Z axis
00530 
00531     // Compute bounding box of the triangle
00532     float minX = std::min({v0.x, v1.x, v2.x});
00533     float maxX = std::max({v0.x, v1.x, v2.x});
00534     float minY = std::min({v0.y, v1.y, v2.y});
00535     float maxY = std::max({v0.y, v1.y, v2.y});
00536     
00537     // Размер плитки
00538     const int tileSize = 32;
00539     
00540     // Обработка каждого блока (плитки)
00541     for (int tileY = static_cast<int>(std::floor(minY)) / tileSize;
00542          tileY <= static_cast<int>(std::floor(maxY)) / tileSize; ++tileY) {
00543         for (int tileX = static_cast<int>(std::floor(minX)) / tileSize;
00544              tileX <= static_cast<int>(std::floor(maxX)) / tileSize; ++tileX) {
00545             
00546             // Координаты плитки в пространстве экрана
00547             int tileStartX = tileX * tileSize;
00548             int tileStartY = tileY * tileSize;
00549             int tileEndX = tileStartX + tileSize - 1;
00550             int tileEndY = tileStartY + tileSize - 1;
00551 
00552             // Ограничиваем плитку по границам треугольника
00553             tileStartX = std::max(tileStartX, static_cast<int>(std::ceil(minX)));
00554             tileEndX = std::min(tileEndX, static_cast<int>(std::floor(maxX)));
00555             tileStartY = std::max(tileStartY, static_cast<int>(std::ceil(minY)));
00556             tileEndY = std::min(tileEndY, static_cast<int>(std::floor(maxY)));
00557             
00558             // Теперь проходим по каждому пикселю внутри плитки
00559             for (int y = tileStartY; y <= tileEndY; ++y) {
00560                 for (int x = tileStartX; x <= tileEndX; ++x) {
00561                     float u, v, w;
00562                     if (ComputeBarycentricCoords(static_cast<float>(x), static_cast<float>(y), v0, v1, v2, u, v, w)) {
00563                         float z = u * v0.z + v * v1.z + w * v2.z;
00564                         if (z < Camera.z_buffer.get(x, y) || !RendMode.Z_Buffer) {
00565                             Uint8 r = rgba[0];
00566                             Uint8 g = rgba[1];
00567                             Uint8 b = rgba[2];
00568                             Uint8 a = rgba[3];
00569 
00570                             SDL_SetRenderDrawColor(SDL_renderer, r, g, b, a);
00571                             SDL_RenderDrawPoint(SDL_renderer, x, y);
00572                             Camera.z_buffer.update(x, y, z);
00573                         }
00574                     }
00575                 }
00576             }
00577         }
00578     }
00579 }
