00008 void PRISM_Painter::DrawThickLine(SDL_Renderer* renderer, int x1, int y1, int x2, int y2, int thickness) {
00009     // Вычисление разности координат
00010     int dx = x2 - x1;
00011     int dy = y2 - y1;
00012 
00013     // Вычисление нормализованного вектора перпендикулярного линии
00014     float length = (float)sqrtf(dx * dx + dy * dy);
00015     float nx = -dy / length;
00016     float ny = dx / length;
00017 
00018     // Рисование параллельных линий
00019     for (int i = -thickness / 2; i <= thickness / 2; ++i) {
00020         int offsetX = (int)(nx * i);
00021         int offsetY = (int)(ny * i);
00022         SDL_RenderDrawLine(renderer, x1 + offsetX, y1 + offsetY, x2 + offsetX, y2 + offsetY);
00023     }
00024 }
