00026 void PRISM_Painter::DrawTriangle(int x1, int y1, int x2, int y2, int x3, int y3, SDL_Renderer* SDL_renderer,
00027     short rgba[4], int thickness = 1) {
00028     SDL_SetRenderDrawColor(SDL_renderer, rgba[0], rgba[1], rgba[2], rgba[3]);
00029     DrawThickLine(SDL_renderer, x1, y1, x2, y2, thickness);
00030     DrawThickLine(SDL_renderer, x2, y2, x3, y3, thickness);
00031     DrawThickLine(SDL_renderer, x3, y3, x1, y1, thickness);
00032 }
