00034 void PRISM_Painter::DrawPoint(PRISM_Window* window, int x, int y)
00035 {
00036     if (!window->isRunning()) {
00037         return;
00038     }
00039 
00040     // переключаем контекст OpenGL окна
00041     SDL_GL_MakeCurrent(window->GetSDLWindow(), window->GetGLContext());
00042     
00043     //отображаем поверх
00044     glDepthFunc(GL_ALWAYS);
00045 
00046     //начало
00047     glBegin(GL_POINTS);
00048     glVertex2f(x, y);
00049     //конец
00050     glEnd();
00051     //возврат режима глубин
00052     glDepthFunc(GL_LESS);
00053 }
